#include "pch.h"
#include "bigint.h"
#include "algorithm.h"
#include <string>

TEST(TestAlgorithm, SmallNumber)
{
	Algorithm* algorithm = Algorithm::GetInstance();
	BigInt zero("0"), one("1"), two("10"), three("11"), four("100"), five("101"), ten("1010");
	
	EXPECT_EQ(true, four == algorithm->Mul(two, two));
	EXPECT_EQ(true, BigInt("10100") == algorithm->Mul(two, ten));
	EXPECT_EQ(true, one == algorithm->MulMod(two, two, three));
	EXPECT_EQ(true, zero == algorithm->MulMod(two, two, two));
	EXPECT_EQ(true, zero == algorithm->PowMod(two, two, two));
	EXPECT_EQ(true, zero == algorithm->PowMod(two, ten, two));
	EXPECT_EQ(true, zero == algorithm->PowMod(one, one, one));
	EXPECT_EQ(true, one == algorithm->PowMod(two, two, three));
	EXPECT_EQ(true, one == algorithm->PowMod(three, two, two));

	EXPECT_EQ(true, five == algorithm->Div(ten, two));
	EXPECT_EQ(true, three == algorithm->Div(ten, three));
}

