TEST(TestAlgorithm, GetInversePhi1024Bits)
{
	Algorithm* algorithm = Algorithm::GetInstance();
	BigInt e("10000000000000001"); // e = 65537
	BigInt phi("");
	BigInt d("");

	EXPECT_EQ(true, d == algorithm->GetEInverse(e, phi));
}

