#include "pch.h"
#include "bigint.h"
#include <string>

TEST(TestCalculation, SmallNumber)
{
	BigInt zero("0"), one("1"), two("10"), three("11"), nine("1001"), ten("1010");
	EXPECT_EQ(true, zero == (zero + zero));
	EXPECT_EQ(true, one == (zero + one));
	EXPECT_EQ(true, two == (one + one));

	EXPECT_EQ(true, zero == (zero - zero));
	EXPECT_EQ(true, zero == (two - two));

	// Not equal bits
	EXPECT_EQ(true, two == (three - one));
	EXPECT_EQ(true, one == (two - one));


	// Equal bits
	EXPECT_EQ(true, one == (three - two));
	EXPECT_EQ(true, nine == (ten - one));
}

TEST(TestCalculation, BigNumberEqualBit)
{
	// 512 bits
	BigInt a512("");
	BigInt b512("");
	BigInt add512("");
	BigInt sub512("");
	BigInt mod512("");

	// 256 bits
	BigInt a256("1111100010000100001001010111110110101101100101000011110100101010110001000011110101001011011001100010001001011111111011000001001011010110011000011010100101001100010100100100000000111100001011111001000011000111000111100101011000110111010100001111101000111111");
	BigInt b256("1110011110001101010010111101001001101110000001101011101110010110110111101011010101101011100000111111011000100010000001110101111010111111010110100010111110110001100011110111110001010100011011111101000101000100101000010100101000101001111011000110101010101110");
	BigInt add256("11110000000010001011100010101000000011011100110101111100011000001101000101111001010110110111010100001100010000001111100110111000110010101101110111101100011111101111000011011110010010000100111110110001000001011101111111010000001100001001111010110010011101101");
	BigInt sub256("1000011110110110110011010101100111111100011011000000110010011111001011000011111011111111000100010110000111101111001001011010000010111000001110111100110011010110000101100001111100111101111111011111110000010011111010000110000001101011001001000111110010001");
	BigInt mod256("1000011110110110110011010101100111111100011011000000110010011111001011000011111011111111000100010110000111101111001001011010000010111000001110111100110011010110000101100001111100111101111111011111110000010011111010000110000001101011001001000111110010001");

	BigInt a1024("");
	BigInt b1024("");
	BigInt add1024("");
	BigInt sub1024("");
	BigInt mod1024("");

	EXPECT_EQ(true, add512 == (a512 + b512));
	EXPECT_EQ(true, sub512 == (a512 - b512));
	EXPECT_EQ(true, mod512 == (a512 % b512));

	EXPECT_EQ(true, add256 == (a256 + b256));
	EXPECT_EQ(true, sub256 == (a256 - b256));
	EXPECT_EQ(true, mod256 == (a256 % b256));

	EXPECT_EQ(true, add1024 == (a1024 + b1024));
	EXPECT_EQ(true, sub1024 == (a1024 - b1024));
	EXPECT_EQ(true, mod1024 == (a1024 % b1024));
}

